import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ChatInput from "./ChatInput";
import TableResponse from "./TableResponse";
import AnswerFeedback from "./AnswerFeedback";

export default function ChatWindow() {
  const { sessionId } = useParams();
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5000/api/session/${sessionId}`)
      .then((res) => res.json())
      .then((data) => setMessages(data.history));
  }, [sessionId]);

  // FIX: sendMessage must be defined
  const sendMessage = async (text) => {
    const res = await fetch(`http://localhost:5000/api/chat/${sessionId}`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ message: text }),
    });

    const data = await res.json();

    setMessages((prev) => [
      ...prev,
      { role: "user", message: text },
      { role: "bot", message: data.reply, table: data.table },
    ]);
  };

  return (
    <div className="space-y-6">
      {messages.map((m, i) => (
        <div key={i} className="bg-gray-50 dark:bg-gray-800 p-4 rounded shadow">
          <p className="font-bold mb-1">{m.role.toUpperCase()}</p>
          <p>{m.message}</p>

          {m.table && <TableResponse tableData={m.table} />}

          <AnswerFeedback />
        </div>
      ))}

      <ChatInput onSend={sendMessage} />
    </div>
  );
}
