import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Sidebar() {
  const [sessions, setSessions] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:5000/api/sessions")
      .then(res => res.json())
      .then(data => setSessions(data));
  }, []);

  const newChat = async () => {
    const res = await fetch("http://localhost:5000/api/new-chat");
    const data = await res.json();
    navigate(`/chat/${data.sessionId}`);
  };

  return (
    <div className="w-64 bg-gray-100 dark:bg-gray-800 p-4 border-r border-gray-300 dark:border-gray-700">

      <button
        onClick={newChat}
        className="w-full bg-blue-600 text-white p-2 rounded hover:bg-blue-700"
      >
        + New Chat
      </button>

      <h3 className="mt-5 mb-2 text-lg font-semibold">Sessions</h3>

      <div className="space-y-2">
        {sessions.map(s => (
          <div
            key={s.id}
            className="p-2 cursor-pointer rounded hover:bg-gray-300 dark:hover:bg-gray-700"
            onClick={() => navigate(`/chat/${s.id}`)}
          >
            {s.title}
          </div>
        ))}
      </div>

    </div>
  );
}
