import { useEffect, useState, useRef } from "react";
import { useParams } from "react-router-dom";
import ChatInput from "./ChatInput";
import TableResponse from "./TableResponse";
import AnswerFeedback from "./AnswerFeedback";
import ErrorMessage from "./ErrorMessage";

export default function ChatWindow() {
  const { sessionId } = useParams();
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState("");
  const listRef = useRef();

  useEffect(() => {
    if (!sessionId) return;
    setError("");
    fetch(`http://localhost:5000/api/session/${sessionId}`)
      .then(res => {
        if (!res.ok) throw new Error("Failed to fetch session");
        return res.json();
      })
      .then(data => setMessages(data.history || []))
      .catch(err => setError(err.message));
  }, [sessionId]);

  useEffect(() => {
    // scroll to bottom when messages change
    if (listRef.current) listRef.current.scrollTop = listRef.current.scrollHeight;
  }, [messages]);

  const sendMessage = async (text) => {
    if (!sessionId) {
      setError("Invalid session.");
      return;
    }
    setError("");
    try {
      const res = await fetch(`http://localhost:5000/api/chat/${sessionId}`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ message: text })
      });

      if (!res.ok) {
        const err = await res.json();
        throw new Error(err.error || "Server error");
      }

      const data = await res.json();
      // append messages
      setMessages(prev => [
        ...prev,
        { role: "user", message: text },
        { role: "bot", message: data.reply, table: data.table }
      ]);
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="flex flex-col h-[75vh] md:h-[80vh]">
      <ErrorMessage message={error} />

      <div ref={listRef} className="flex-1 overflow-auto space-y-4 p-2">
        {messages && messages.length === 0 && (
          <div className="text-gray-500">No messages yet. Ask something!</div>
        )}

        {messages.map((m, idx) => (
          <div key={idx} className="p-4 rounded bg-white dark:bg-gray-800 shadow-sm">
            <div className="text-xs text-gray-500 mb-1">{m.role.toUpperCase()}</div>
            <div className="mb-2">{m.message}</div>

            {m.table && <TableResponse tableData={m.table} />}

            <div className="mt-2"><AnswerFeedback /></div>
          </div>
        ))}
      </div>

      <div className="mt-3">
        <ChatInput onSend={sendMessage} />
      </div>
    </div>
  );
}
